import React, { Component } from 'react';

class Input extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            value: ''
         }
    }

    handleChange = (event) => {
        this.setState({value: event.target.value});
      }
    render() { 
        const randomVerb = this.props.data[Math.floor(Math.random() * this.props.data.length)];
        const randomPerson = Object.entries(randomVerb)[ Math.floor(Math.random() * 7) + 7 ]
        return ( 
            <div>
                <p><b>Person:</b>
                    { randomPerson[0] === 'form_1s' &&
                        <span> Singular, 1st person</span>
                    }
                    { randomPerson[0] === 'form_2s' &&
                        <span> Singular, 2nd person</span>
                    }
                    { randomPerson[0] === 'form_3s' &&
                        <span> Singular, 3rd person</span>
                    }
                    { randomPerson[0] === 'form_1p' &&
                        <span> Plural, 1st person</span>
                    }
                    { randomPerson[0] === 'form_2p' &&
                        <span> Plural, 2nd person</span>
                    }
                    { randomPerson[0] === 'form_3p' &&
                        <span> Plural, 3rd person</span>
                    }
                    { randomPerson[0] === 'gerund' &&
                        <span> Gerund</span>
                    }
                </p>
                <p>{randomPerson[1]}</p>
                <input type="text" value={this.state.value} onChange={this.handleChange} />
                <button>Check</button>
            </div>
         );
    }
}

export default Input;
