import React, { Component } from 'react';
import './app.css'
import miniData from './miniData';
import ConjugatorContainer from './components/conjugatorPage/ConjugatorContainer';
import { Route, Link } from 'react-router-dom';
import OptionsContainer from './components/optionsPage/OptionsContainer';

class App extends Component {
  constructor() {
    super();
    this.state = {
      data: miniData,
      verbTenses: []
    };
  }

  filterData = event => {
    event.preventDefault();
    const test = Object.values(miniData).filter(
      verb => verb.infinitive === 'hablar'
    );
    this.setState({
      data: test
    });
  };

  setTenses = event => {
    this.setState({
      verbTenses: event.target.value
    });
    console.log('Clicked!');
  };

  render() {
    return (
      <div>
        <h2>Bienvenidos, a la <Link to="/" className='logo'>Spanish Conjugator</Link></h2>
        <p><b>What would you like to do?</b></p>
        <Link to='/random' className='box'>
          <p><b>Practice with random verbs</b></p>
          <p>Translate different verbs, tenses, and forms</p>
        </Link>
        <div>
          <Route
            exact
            path="/"
            render={props => (
              <ConjugatorContainer
                {...props}
                setTenses={this.setTenses}
                data={this.state.data}
              />
            )}
          />
        </div>
        <div>
          <Route
            path="/options"
            render={props => (
              <OptionsContainer {...props} filterData={this.filterData} />
            )}
          />
        </div>
      </div>
    );
  }
}

export default App;
