import React, { Component } from 'react';

class ConjugatorInput extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }

    handleChange = (event) => {
        this.setState({value: event.target.value});
      }
    render() { 
        const { number, randomVerb } = this.props
        console.log(this.props)
        return ( 
            <div>
                <input 
                    type="text"
                    name="answer"
                    onChange={this.handleChange}
                />
                <button>Check</button>
                <div>Answer: {this.props.randomVerb[this.props.number]}</div>
            </div>
         );
    }
}

export default ConjugatorInput;
